sp_widget.client_script:
function guideHomePageSelectorCtrl(appManager, $timeout, $location) {
    /* widget controller */
    var c = this;
    appManager.setTitle('home');

    $timeout(function() {
        $location.url("/guide?id=city_guide_allow_access");
    }, 2000);

}

