var queryString = "eb_category_idISNOTEMPTY^descriptionLIKEqueer^ORdescriptionLIKEsex ^ORdescriptionLIKEporn^ORdescriptionLIKEnude^ORdescriptionLIKEtopless^ORdescriptionLIKElust";
var gr = new GlideRecord('x_snc_guide_event');
gr.addQuery('sys_id', current.sys_id);
gr.addEncodedQuery(queryString);
gr.setLimit(1);
gr.query();
if(gr.next()) {
  //found a yellow flag, set state = 'pending' manual approval
  current.state = "Pending";
 } else {
  current.state = "Active";
 }

